mapper.select:
SELECT no, type, time, calorie, reg_date as regDate, user_id as userId 
FROM exercise 
WHERE reg_date = #{regDate} AND user_id = #{loginUserId};

SELECT no, type, time, calorie, reg_date as regDate, user_id as userId
FROM exercise
WHERE no = #{exerciseNo};

mapper.update:
UPDATE exercise
SET type = #{type}, time = #{time}, calorie = #{calorie}
WHERE no = #{no};
